import { createSlice } from "@reduxjs/toolkit";

const initialState = {
    items:[],
    totalQuantity:0,
    totalAmount:0
}

const cartSlice = createSlice({
    name: 'cart',
    initialState:initialState,
    reducers:{
        addItemToCart (state,action){
            const newItem = action.payload;
            const existingItem = state.items.find((item) => item.id === newItem.id);
            state.totalQuantity++
            if(!existingItem){
                state.items.push({
                    id:newItem.id,
                    itemPrice:newItem.price,
                    itemQuntity: 1,
                    itemTotal :newItem.price,
                    itemName: newItem.title  
                });
            } else{
                existingItem.itemQuntity++;
                existingItem.itemPrice = existingItem.itemTotal + newItem.itemPrice
            }
        },
        removeItemToCart (state,action){
            const id = action.payload;
            const removingItem = state.items.find((item) => item.id === id);
            state.totalQuantity--
            if(removingItem === 1){
                state.items = state.items.filter((item) => item.id !== id);
            } else{ 
                removingItem.itemQuntity--;
            }
        }
    }
});

export default cartSlice.reducer
export const cartAction = cartSlice.actions;
