import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  items: [],
  totalQuantity: 0,
};

const cartSlice = createSlice({
  name: 'cart',
  initialState: initialState,
  reducers: {
    replaceCart(state, action) {
      state.totalQuantity = action.payload.totalQuantity;
      state.items = action.payload.items;
    },
    addItemToCart(state, action) {
      const newItem = action.payload;
      const existingItem = state.items.find((item) => item.id === newItem.id);

      state.totalQuantity++;

      if (!existingItem) {
        state.items.push({
          id: newItem.id,
          itemPrice: newItem.price,
          itemQuantity: 1,   // Fixed typo: itemQuntity to itemQuantity
          itemTotal: newItem.price,
          itemName: newItem.title,
        });
      } else {
        existingItem.itemQuantity++;  // Fixed typo: itemQuntity to itemQuantity
        existingItem.itemTotal = existingItem.itemQuantity * existingItem.itemPrice; // Fixed typo: itemQuntity to itemQuantity
      }
    },
    removeItemToCart(state, action) {
      const id = action.payload;
      const removingItem = state.items.find((item) => item.id === id);

      state.totalQuantity--;

      if (removingItem.itemQuantity === 1) { // Fixed typo: itemQuntity to itemQuantity
        state.items = state.items.filter((item) => item.id !== id);
      } else {
        removingItem.itemQuantity--; // Fixed typo: itemQuntity to itemQuantity
      }
    },
  },
});

export default cartSlice.reducer;
export const cartAction = cartSlice.actions;
