import { createSlice } from "@reduxjs/toolkit";

const initialState = {
    cartInvisible : false
}

const uiSlice = createSlice({
    name:'ui',
    initialState: initialState, 
    reducers:{
        toogle (state) {
            state.cartInvisible = !state.cartInvisible
        }
    }

});


export default uiSlice.reducer
export const uiActions = uiSlice.actions;
