import { Fragment, useEffect } from 'react';
import Cart from './components/Cart/Cart';
import Layout from './components/Layout/Layout';
import Products from './components/Shop/Products';
import { useSelector, useDispatch } from 'react-redux';
import { uiActions } from './store/ui-slice';
import Notification from './components/UI/Notification';

function App() {
  const cartInvisible = useSelector((state) => state.ui.cartInvisible);
  const cart = useSelector((state) => state.cart);
  const dispatch = useDispatch();
  const notifiaction = useSelector((state) => state.ui.notifiaction);

  let isNitial = true;

  useEffect(() => {
    const sendCart = async () => {
      dispatch(uiActions.showNotification({
        status:'pending',
        title:'Sending...',
        message:'Sending cart data!'
      }))
      const response = await fetch('https://learn-redux-f752f-default-rtdb.firebaseio.com/cart.json',{
        method:'PUT',
        body:JSON.stringify(cart)
      });

      if(!response.ok){
        throw new Error('Sending cart data failed.')
      }

      dispatch(uiActions.showNotification({
        status:'succes',
        title:'Succes!',
        message:'Sent cart data succesfully!'
      }))
    }

    if(isNitial){
      isNitial = false;
      return;
    }

    sendCart().catch((error)=>{
      dispatch(uiActions.showNotification({
        status:'error',
        title:'Error!',
        message:'Sending cart data failed!'
      }))
    })

  },[cart, dispatch])

  return (
    <Fragment>
      {notifiaction && <Notification status={notifiaction.status} title={notifiaction.title} message={notifiaction.message}/>}
      <Layout>
        {cartInvisible && <Cart />}
        <Products />
      </Layout>
    </Fragment>
  );
}

export default App;
